      },
      queryData: [
        /*{loc: "主教学楼", id: "101", num1: 137, num2:82, personNum: 0, banjis: [], rebuilds: [], teachers: []},
        {loc: "主教学楼", id: "102", num1: 136, num2:82, personNum: 0, banjis: [], rebuilds: [], teachers: []},
