        banji: 4,
        sum: 31,
        disabled: false
      }],
      rebuilds: [],
      teachers: [],
      timer: ''
    };
  },
  created: function created() {},
  mounted: function mounted() {
    this.init(); //获取数据

    this.clickList();
    this.clickBar();
  },
  methods: {
    /*初始化*/
    init: function init() {
      this.setTermList();
    },

    /**/
    setTermList: function setTermList() {
      var _this = this;

      this.axios.get("/exam/termList/").then(function (res) {
        console.log(res.data);

        if (res.data.flag === true) {
          _this.termList = res.data.data;
        }
      }).finally(function () {});
    },
    setDate: function setDate() {
      var _this2 = this;

      if (this.exam.term.term === '' || this.exam.term.term === null) {
        this.pickerOptions = Object.assign({}, this.pickerOptions, {
          disabledDate: function disabledDate(time) {
            return false;
          }
        });
        return;
      }

      this.pickerOptions = Object.assign({}, this.pickerOptions, {
        disabledDate: function disabledDate(time) {
          //禁用这一天的日期  如需禁用之前之后，可把=== 替换成 > 或 <
          var disable = time < new Date(_this2.exam.term.startTime) - 24 * 60 * 60 * 1000; //循环后端返回的数据，禁用时间大于contractStartTime小于contractEndTime，注意contractStartTime要减去一天，才能真正禁用contractStartTime这一天。

          /*data.data.forEach((item) => {
              disable =
                  disable ||
                  (time.getTime() > new Date(item.contractStartTime).getTime() - 8.64e7 &&
                      time.getTime() < new Date(item.contractEndTime).getTime()); //减去一天8.64e7
          });*/

          disable = disable || time.getTime() > new Date(_this2.exam.term.endTime).getTime() + 24 * 60 * 60 * 1000;
          return disable;
        }
      });
    },
    setCollegeList: function setCollegeList() {
      var _this3 = this;

      console.log(this.exam.term);
      this.axios.post("/exam/collegeList/", this.exam.term).then(function (res) {
        console.log(res.data);

        if (res.data.flag === true) {
          _this3.collegeList = res.data.data;
        }
      }).finally(function () {});
    },
    setProfessionList: function setProfessionList() {
      var _this4 = this;

      var obj = {
        college: this.exam.college,
        term: this.exam.term
      };
      console.log(this.exam.college);
      this.axios.post("/exam/professionList/", obj).then(function (res) {
        console.log(res.data);

        if (res.data.flag === true) {
          _this4.professionList = res.data.data;
        }
      }).finally(function () {});
    },
    setGradeList: function setGradeList() {
      this.gradeList = [];
      console.log(this.exam.profession.profession);

      for (var i in this.professionList) {
        if (this.professionList[i].profession === this.exam.profession.profession) {
          this.gradeList.push(this.professionList[i]);
        }
      }
    },
    setCourseList: function setCourseList() {
      var _this5 = this;

      var obj = {
        college: this.exam.college,
        term: this.exam.term,
        profession: this.exam.profession
      };
      console.log("this.exam.profession", this.exam.profession);
      this.axios.post("/exam/courseList/", obj).then(function (res) {
        console.log(res.data);

        if (res.data.flag === true) {
          _this5.courseList = res.data.data;
        }
      }).finally(function () {});
    },
    setExamTime: function setExamTime() {
      this.exam.startTime = this.timeList[0];
      this.exam.endTime = this.timeList[1];
      var d = this.exam.term.startTime + ' 00:00:00';
      console.log("this.exam.startTime", this.exam.startTime);
      console.log("this.exam.endTime", this.exam.endTime);
      console.log("this.exam.term.startTime", d);
      var d1 = new Date(this.exam.startTime);
      var d2 = new Date(this.exam.endTime);
      var d3 = new Date(d);
      console.log("d3", d3);
      console.log("d1", d1);
      /*console.log(parseInt(d2 - d1));//两个时间相差的毫秒数
      console.log(parseInt(d2 - d1) / 1000);//两个时间相差的秒数*/

      console.log(parseInt(d1 - d3) / 1000 / 60 / 24 / 7 + 1);

      if (parseInt(d1 - d3) > 0) {
        this.exam.week = parseInt(parseInt(d1 - d3) / 1000 / 60 / 60 / 24 / 7) + 1;
      } else {
        this.exam.week = 1;
      }

      if (parseInt(d2 - d1) > 0) {
        this.exam.time = parseInt(d2 - d1) / 1000 / 60; //两个时间相差的分钟数
      } else if (parseInt(d2 - d1) === 0) {
        this.exam.time = 0;
      }

      console.log("this.exam.week", this.exam.week);
      console.log("this.exam.time", this.exam.time);
    },
    clickBar: function clickBar() {
      var handler = document.querySelector('.handler');
      var left_box = document.querySelector('.left-box');
      var right_box = document.querySelector('.right-box');
      var screenWidth = window.screen.width;
      handler.addEventListener('click', function () {
        if (!this.classList.contains('close')) {
          left_box.style.width = 0;
          right_box.style.maxWidth = screenWidth + 'px';
          this.classList.add('close');
        } else {
          left_box.style.width = 260 + 'px';
          right_box.style.maxWidth = screenWidth - 260 + 'px';
          this.classList.remove('close');
        }
      });
    },
    clickList: function clickList() {
      // 获取要操作的元素
      var items = document.querySelectorAll('.item');
      var current_tag = document.querySelector('.current-tag');
      var rm = document.querySelectorAll('.rm'); // 设置选中项的样式

      function setActive() {
        items.forEach(function (item) {
          item.classList.remove('active');
        });
        this.classList.add('active');
        current_tag.innerText = this.innerText;
      } // 为每一个li设置点击事件


      var _loop = function _loop(ix) {
        items[ix].addEventListener('click', function () {
          for (var i = 0; i < items.length; i++) {
            items[i].classList.remove('active');
            rm[i].style.display = 'none';
          }

          this.classList.add('active');
          current_tag.innerText = this.innerText;
          rm[ix].style.display = 'block';
        });
      };

      for (var ix = 0; ix < items.length; ix++) {
        _loop(ix);
      }
    },
    querySearchCollege: function querySearchCollege(queryString, cb) {
      var collegeList = this.collegeList;
      var results = queryString ? collegeList.filter(this.createFilter(queryString)) : collegeList; // 调用 callback 返回建议列表的数据

      cb(results);
    },
    createFilter: function createFilter(queryString) {
      return function (item) {
        return item.value.toLowerCase().indexOf(queryString.toLowerCase()) === 0;
      };
    },
    handleSelect: function handleSelect(item) {
      console.log(item);
    },
    loadAll: function loadAll() {}
  },
  watch: {
    //当前组件的侦听器
    'exam.term': function examTerm(newVal, oldVal) {
      console.log("exam.term newVal:", newVal, "oldVal:", oldVal);

      if (JSON.stringify(newVal) !== JSON.stringify(oldVal)) {
        this.exam.college = {};
        this.setDate();
        this.setCollegeList();
      }
    },
    'exam.college': function examCollege(newVal, oldVal) {
      console.log("exam.college newVal:", newVal, "oldVal:", oldVal);

      if (JSON.stringify(newVal) !== JSON.stringify(oldVal)) {
        this.exam.profession = {};
        this.setProfessionList();
      }
    },
    'exam.profession': function examProfession(newVal, oldVal) {
      console.log("exam.profession newVal:", newVal, "oldVal:", oldVal);

      if (JSON.stringify(newVal) !== JSON.stringify(oldVal)) {
        this.exam.grade = {};
        this.setGradeList();
      }
    },
    'exam.grade': function examGrade(newVal, oldVal) {
      console.log("exam.grade newVal:", newVal, "oldVal:", oldVal);

      if (JSON.stringify(newVal) !== JSON.stringify(oldVal)) {
        this.exam.course = {};
        this.setCourseList();
      }
    },
    'timeList': function timeList(newVal, oldVal) {
      console.log("timeList newVal:", newVal, "oldVal:", oldVal);

      if (JSON.stringify(newVal) !== JSON.stringify(oldVal)) {
        this.setExamTime();
      }
    }
  }
};
